from collections.abc import Generator
from contextlib import AbstractContextManager, ExitStack, contextmanager
from typing import TypeVar

_T = TypeVar("_T", covariant=True)


class CommandContextMixIn:
    """
    Provides a managed main execution context using ExitStack.
    Ensures resources are properly handled within the main context.
    """

    def __init__(self) -> None:
        super().__init__()
        self._in_main_context: bool = False
        self._main_context: ExitStack = ExitStack()

    @contextmanager
    def main_context(self) -> Generator[None, None, None]:
        if self._in_main_context:
            raise RuntimeError("Already inside main context")

        self._in_main_context = True
        try:
            with self._main_context:
                yield
        finally:
            self._in_main_context = False

    def enter_context(self, context_provider: AbstractContextManager[_T]) -> _T:
        if not self._in_main_context:
            raise RuntimeError("enter_context() called outside main context")

        return self._main_context.enter_context(context_provider)